# Boundary Value Analysis
def test_boundary_value_case1():
    assert sum_strings("1", "1") == "2"

def test_boundary_value_case2():
    assert sum_strings("999999", "1") == "1000000"

def test_boundary_value_case3():
    assert sum_strings("1", "999999") == "1000000"

def test_boundary_value_case4():
    assert sum_strings("999999", "999999") == "1999998"

def test_boundary_value_case5():
    assert sum_strings("1", "") == ""

# Partition Testing
def test_partition_case1():
    assert sum_strings("123", "456") == "579"

def test_partition_case2():
    assert sum_strings("789", "321") == "1110"

def test_partition_case3():
    assert sum_strings("0", "987654321") == "987654321"

def test_partition_case4():
    assert sum_strings("123456789", "0") == "123456789"

def test_partition_case5():
    assert sum_strings("111111111", "888888889") == "1000000000"

# Use Case Testing
def test_use_case_case1():
    assert sum_strings("12345678901234567890", "98765432109876543210") == "111111111011111111100"

def test_use_case_case2():
    assert sum_strings("99999999999999999999", "1") == "100000000000000000000"

def test_use_case_case3():
    assert sum_strings("1", "99999999999999999999") == "100000000000000000000"

def test_use_case_case4():
    assert sum_strings("123456789", "987654321") == "1111111110"

def test_use_case_case5():
    assert sum_strings("0", "0") == "0"

